-- Create "todos" table
CREATE TABLE "todos" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "text" character varying NOT NULL, "done" boolean NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "user_owned" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "todos_users_owned" FOREIGN KEY ("user_owned") REFERENCES "users" ("id") ON DELETE NO ACTION);
